<script>
import { documentToHtmlString } from "@contentful/rich-text-html-renderer"

  export default {
    head() {
      return {
        title: `Race & Health | ${this.page.fields.title}`
      }
    },
    data() {
      return {
        slug: "quarterly"
      }
    },
    computed: {
      quarterly() {
        return this.$store.state.quarterly
      },
      page() {
        let page = this.$store.state.pages.find(
          el => el.fields.slug === this.slug
        )
        return page
      },
      pageHtml() {
        let richTextHtml = documentToHtmlString(this.page.fields.content)
        return richTextHtml
      }
    },
    mounted() {
      this.$store.commit('graphics/setCircle',
        {
          width: 40,
          bottom: 50,
          left: -20
        })    
    }
  }
</script>

